import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Typography, Card, CardContent, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField } from '@mui/material';

const Requirements = () => {
  const [posts, setPosts] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [price, setPrice] = useState('');
  const [open, setOpen] = useState(false);

  useEffect(() => {
    // Fetch posts from connected users
    const fetchPosts = async () => {
      try {
        const token = localStorage.getItem('token'); // Retrieve the token from localStorage
        const response = await axios.get('http://localhost:5000/api/connected-posts', {
          headers: {
            Authorization: `Bearer ${token}`, // Pass the token in the Authorization header
          },
        });
        setPosts(response.data); // Update state with the fetched posts
      } catch (error) {
        console.error('Error fetching posts:', error.response ? error.response.data : error.message);
      }
    };
    fetchPosts();
  }, []);

  const handleSendQuote = (item) => {
    setSelectedItem(item);
    setOpen(true);
  };

  const handleSubmitQuote = async () => {
    try {
      await axios.post('http://localhost:5000/api/send-quote', {
        sender: localStorage.getItem('userId'),
        receiver: selectedItem.userId,
        item: { ...selectedItem, price },
      });
      setOpen(false);
      alert('Quote sent successfully!');
    } catch (error) {
      alert(error.message);
    }
  };

  const handleDiscard = () => {
    if (window.confirm('Are you sure you want to discard?')) {
      setOpen(false);
      setSelectedItem(null);
    }
  };

  return (
    <Box sx={{ py: 4, textAlign: 'center' }}>
      <Typography variant="h4">Requirements</Typography>
      <Box sx={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: 2, mt: 4 }}>
        {posts.map((post) => (
          <Card key={post._id} sx={{ width: 300, bgcolor: 'black', color: 'white', boxShadow: '0px 0px 20px neonBlue' }}>
            <CardContent>
              <Typography variant="h6">{post.shopName}</Typography>
              <ul>
                {post.items.map((item) => (
                  <li key={item.name}>
                    {item.name} - {item.quantity} - {new Date(item.deadline).toLocaleDateString()}
                  </li>
                ))}
              </ul>
              <Button variant="contained" color="primary" onClick={() => handleSendQuote(post)}>
                Send Quote
              </Button>
            </CardContent>
          </Card>
        ))}
      </Box>

      {/* Dialog for Sending Quote */}
      <Dialog open={open} onClose={() => setOpen(false)}>
        <DialogTitle>Send Quote</DialogTitle>
        <DialogContent>
          <Typography>Name: {selectedItem?.name}</Typography>
          <Typography>Quantity: {selectedItem?.quantity}</Typography>
          <TextField
            label="Price"
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button color="error" onClick={handleDiscard}>
            Discard
          </Button>
          <Button color="primary" onClick={handleSubmitQuote}>
            Submit
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Requirements;
