import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Card,
  CardContent,
  Typography,
  TextField,
  Button,
  Grid,
  Snackbar,
  IconButton,
  Switch,
} from '@mui/material';
import MuiAlert from '@mui/material/Alert';
import CloseIcon from '@mui/icons-material/Close';

const Profile = () => {
  const [profile, setProfile] = useState({});
  const [formData, setFormData] = useState({
    ownerName: '',
    shopLocation: '',
    shopCategory: '',
  });
  const [darkMode, setDarkMode] = useState(false);
  const [showSnackbar, setShowSnackbar] = useState(false);
  const [verificationMessage, setVerificationMessage] = useState('');

  useEffect(() => {
    // Fetch profile
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/profile', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProfile(response.data);
      setFormData({
        ownerName: response.data.ownerName,
        shopLocation: response.data.shopLocation,
        shopCategory: response.data.shopCategory,
      });
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await axios.post(
        'http://localhost:5000/api/profile/update',
        formData,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert(response.data.message);
    } catch (error) {
      console.error(error);
      alert('Error updating profile');
    }
  };

  const sendVerificationEmail = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.post(
        'http://localhost:5000/api/profile/verify',
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setVerificationMessage(response.data.message);
      setShowSnackbar(true);
    } catch (error) {
      console.error(error);
      setVerificationMessage('Failed to send verification email');
      setShowSnackbar(true);
    }
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div
      style={{
        backgroundColor: darkMode ? '#121212' : '#f4f4f4',
        color: darkMode ? '#fff' : '#000',
        minHeight: '100vh',
        padding: '20px',
        transition: 'all 0.3s',
      }}
    >
      <Grid container justifyContent="flex-end">
        <Typography>Dark Mode</Typography>
        <Switch checked={darkMode} onChange={toggleDarkMode} />
      </Grid>

      <Grid container justifyContent="center">
        <Card
          style={{
            width: '80%',
            maxWidth: 600,
            backgroundColor: darkMode ? '#1e1e1e' : '#fff',
            color: darkMode ? '#fff' : '#000',
          }}
        >
          <CardContent>
            <Typography variant="h5" gutterBottom>
              Profile Details
            </Typography>
            <form onSubmit={handleSubmit}>
              <Grid container spacing={2}>
                {/* Read-only fields */}
                <Grid item xs={12}>
                  <TextField
                    label="Email"
                    value={profile.email}
                    fullWidth
                    InputProps={{ readOnly: true }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    label="Mobile"
                    value={profile.mobile}
                    fullWidth
                    InputProps={{ readOnly: true }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    label="Shop Name"
                    value={profile.shopName}
                    fullWidth
                    InputProps={{ readOnly: true }}
                  />
                </Grid>

                {/* Editable fields */}
                <Grid item xs={12}>
                  <TextField
                    label="Owner Name"
                    name="ownerName"
                    value={formData.ownerName}
                    onChange={handleChange}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    label="Shop Location"
                    name="shopLocation"
                    value={formData.shopLocation}
                    onChange={handleChange}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    label="Shop Category"
                    name="shopCategory"
                    value={formData.shopCategory}
                    onChange={handleChange}
                    fullWidth
                  />
                </Grid>
              </Grid>

              {/* Submit Button */}
              <Grid container justifyContent="space-between" alignItems="center" style={{ marginTop: '20px' }}>
                <Button type="submit" variant="contained" color="primary">
                  Update Profile
                </Button>
                <Button
                  variant="outlined"
                  color="secondary"
                  onClick={sendVerificationEmail}
                >
                  Verify Email
                </Button>
              </Grid>
            </form>
          </CardContent>
        </Card>
      </Grid>

      {/* Snackbar for verification messages */}
      <Snackbar
        open={showSnackbar}
        autoHideDuration={6000}
        onClose={() => setShowSnackbar(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <MuiAlert
          onClose={() => setShowSnackbar(false)}
          severity="info"
          sx={{ width: '100%' }}
        >
          {verificationMessage}
        </MuiAlert>
      </Snackbar>
    </div>
  );
};

export default Profile;
