import React, { useState } from "react";
import {
  Box,
  Button,
  TextField,
  Typography,
  IconButton,
  Paper,
  Grid,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";
import axios from "axios";
import jwtDecode from "jwt-decode"; // For decoding JWT token

const PostForm = () => {
  const [rows, setRows] = useState([{ name: "", quantity: "", deadline: "" }]);
  const userId = jwtDecode(localStorage.getItem("token")).id; // Extracting user ID from JWT

  const handleInputChange = (index, field, value) => {
    const updatedRows = [...rows];
    updatedRows[index][field] = value;
    setRows(updatedRows);
  };

  const handleAddRow = () => {
    setRows([...rows, { name: "", quantity: "", deadline: "" }]);
  };

  const handleDeleteRow = (index) => {
    const updatedRows = rows.filter((_, i) => i !== index);
    setRows(updatedRows);
  };

  const handleSubmit = async () => {
    try {
      const payload = {
        userId,
        items: rows,
      };
      const response = await axios.post("http://localhost:5000/api/posts", payload);
      console.log("Response:", response.data);
      alert("Data posted successfully!");
    } catch (error) {
      console.error("Error posting data:", error);
      alert("Failed to post data.");
    }
  };

  return (
    <Box
      sx={{
        padding: 3,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 3,
      }}
    >
      {/* Header */}
      <Typography variant="h4" component="h1" gutterBottom>
        Post Items
      </Typography>

      <Paper
        elevation={3}
        sx={{
          padding: 3,
          maxWidth: 800,
          width: "100%",
        }}
      >
        {rows.map((row, index) => (
          <Grid container spacing={2} key={index} alignItems="center">
            {/* Name Field */}
            <Grid item xs={4}>
              <TextField
                label="Name"
                fullWidth
                value={row.name}
                onChange={(e) =>
                  handleInputChange(index, "name", e.target.value)
                }
              />
            </Grid>

            {/* Quantity Field */}
            <Grid item xs={3}>
              <TextField
                label="Quantity"
                fullWidth
                type="number"
                value={row.quantity}
                onChange={(e) =>
                  handleInputChange(index, "quantity", e.target.value)
                }
              />
            </Grid>

            {/* Deadline Field */}
            <Grid item xs={4}>
              <TextField
                label="Deadline"
                fullWidth
                type="date"
                InputLabelProps={{ shrink: true }}
                value={row.deadline}
                onChange={(e) =>
                  handleInputChange(index, "deadline", e.target.value)
                }
              />
            </Grid>

            {/* Delete Row Button */}
            <Grid item xs={1}>
              <IconButton
                color="error"
                onClick={() => handleDeleteRow(index)}
                disabled={rows.length === 1} // Prevent deleting the last row
              >
                <DeleteIcon />
              </IconButton>
            </Grid>
          </Grid>
        ))}

        {/* Add Row Button */}
        <Box sx={{ textAlign: "center", marginTop: 2 }}>
          <Button
            startIcon={<AddIcon />}
            variant="contained"
            color="primary"
            onClick={handleAddRow}
          >
            Add Row
          </Button>
        </Box>
      </Paper>

      {/* Submit Button */}
      <Button
        variant="contained"
        color="success"
        size="large"
        onClick={handleSubmit}
      >
        Submit
      </Button>
    </Box>
  );
};

export default PostForm;
