import React, { useEffect, useState } from 'react';
import { useTheme } from '../context/ThemeContext'; // Adjust the path if necessary
import {
  Box,
  Card,
  CardContent,
  Typography,
  TextField,
  Button,
  MenuItem,
  CircularProgress,
  Grid,
  Alert,
} from '@mui/material';
import axios from 'axios';

const SHOP_CATEGORIES = [
  'Grocery',
  'Electronics',
  'Clothing',
  'Bakery',
  'Restaurant',
  'Stationery',
  'Hardware',
  'Jewelry',
  'Pharmacy',
  'Cosmetics',
  'Furniture',
  'Bookstore',
  'Sports',
  'Toys',
  'Other',
];

const Profile = () => {
  const { isDarkMode } = useTheme(); // Get dark mode state from context
  const [profile, setProfile] = useState({});
  const [formData, setFormData] = useState({
    ownerName: '',
    shopLocation: '',
    shopCategory: '',
  });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await axios.get('http://localhost:5000/api/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(response.data);
        setFormData({
          ownerName: response.data.ownerName,
          shopLocation: response.data.shopLocation,
          shopCategory: response.data.shopCategory,
        });
        setLoading(false);
      } catch (error) {
        console.error('Error fetching profile:', error);
        setLoading(false);
      }
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await axios.post(
        'http://localhost:5000/api/profile/update',
        formData,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setMessage({ type: 'success', text: response.data.message });
    } catch (error) {
      console.error(error);
      setMessage({ type: 'error', text: 'Error updating profile' });
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        backgroundColor: isDarkMode ? '#121212' : '#f5f5f5',
        padding: 3,
      }}
    >
      <Card
        sx={{
          width: '90%',
          maxWidth: 600,
          backgroundColor: isDarkMode ? '#1e1e1e' : '#ffffff',
          color: isDarkMode ? '#ffffff' : '#000000',
          boxShadow: 3,
        }}
      >
        <CardContent>
          <Typography variant="h5" sx={{ marginBottom: 3, fontWeight: 'bold' }}>
            Profile Details
          </Typography>
          {message && (
            <Alert severity={message.type} sx={{ marginBottom: 2 }}>
              {message.text}
            </Alert>
          )}
          <form onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  label="Email"
                  value={profile.email}
                  fullWidth
                  InputProps={{
                    readOnly: true,
                    endAdornment: (
                      <Typography sx={{ color: 'grey', paddingLeft: 1 }}>
                        <strong>Blocked</strong>
                      </Typography>
                    ),
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="Mobile"
                  value={profile.mobile}
                  fullWidth
                  InputProps={{
                    readOnly: true,
                    endAdornment: (
                      <Typography sx={{ color: 'grey', paddingLeft: 1 }}>
                        <strong>Blocked</strong>
                      </Typography>
                    ),
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="Shop Name"
                  value={profile.shopName}
                  fullWidth
                  InputProps={{
                    readOnly: true,
                    endAdornment: (
                      <Typography sx={{ color: 'grey', paddingLeft: 1 }}>
                        <strong>Blocked</strong>
                      </Typography>
                    ),
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="Owner Name"
                  name="ownerName"
                  value={formData.ownerName}
                  onChange={handleChange}
                  fullWidth
                  required
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="Shop Location"
                  name="shopLocation"
                  value={formData.shopLocation}
                  onChange={handleChange}
                  fullWidth
                  required
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  select
                  label="Shop Category"
                  name="shopCategory"
                  value={formData.shopCategory}
                  onChange={handleChange}
                  fullWidth
                  required
                >
                  {SHOP_CATEGORIES.map((category, index) => (
                    <MenuItem key={index} value={category}>
                      {category}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
            </Grid>
            <Button type="submit" variant="contained" fullWidth sx={{ marginTop: 3 }}>
              Update Profile
            </Button>
          </form>
        </CardContent>
      </Card>
    </Box>
  );
};

export default Profile;
