import React, { useEffect, useState } from 'react';
import { Card, CardContent, Typography, Button, Grid, Box } from '@mui/material';
import axios from 'axios';

const ConnectionRequests = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const token = localStorage.getItem('token'); // Assuming the token is stored in localStorage
      const { data } = await axios.get('http://localhost:5000/api/connections/requests', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRequests(data);
    } catch (error) {
      console.error(error.response?.data || error.message);
    }
  };

  const handleAccept = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/api/connections/accept/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchRequests(); // Refresh the list
    } catch (error) {
      console.error(error.response?.data || error.message);
    }
  };

  const handleReject = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/api/connections/reject/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchRequests(); // Refresh the list
    } catch (error) {
      console.error(error.response?.data || error.message);
    }
  };

  return (
    <Box p={3}>
      <Typography variant="h4" gutterBottom>
        Connection Requests
      </Typography>
      <Grid2 container spacing={3}>
        {requests.length > 0 ? (
          requests.map((request) => (
            <Grid2 item xs={12} sm={6} md={4} key={request._id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{request.ownerName}</Typography>
                  <Typography color="textSecondary">{request.shopName}</Typography>
                  <Typography color="textSecondary">{request.email}</Typography>
                  <Box mt={2}>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={() => handleAccept(request._id)}
                      style={{ marginRight: '10px' }}
                    >
                      Accept
                    </Button>
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => handleReject(request._id)}
                    >
                      Reject
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            </Grid2>
          ))
        ) : (
          <Typography>No connection requests found.</Typography>
        )}
      </Grid2>
    </Box>
  );
};

export default ConnectionRequests;
