import React, { useState, useEffect } from "react";
import { Box, Button, TextField, Typography, Grid } from "@mui/material";
import axios from "axios";

const PostForm = () => {
  const [shopName, setShopName] = useState(""); // Pre-filled from JWT token
  const [items, setItems] = useState([{ name: "", quantity: "", deadline: "" }]);
  const token = localStorage.getItem("jwtToken"); // Get JWT from local storage

  // Decode shopName and userId from JWT
  useEffect(() => {
    if (token) {
      const decodedToken = JSON.parse(atob(token.split(".")[1])); // Decode JWT payload
      setShopName(decodedToken.shopName); // Set shop name from JWT
    }
  }, [token]);

  const handleAddRow = () => {
    setItems([...items, { name: "", quantity: "", deadline: "" }]);
  };

  const handleChange = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] = value;
    setItems(newItems);
  };

  const handleSubmit = async () => {
    const userId = JSON.parse(atob(token.split(".")[1])).id; // Decode user ID from JWT

    const data = {
      userId,
      shopName,
      createdAt: new Date().toISOString(),
      items,
    };

    try {
      const response = await axios.post("http://localhost:5000/api/posts", data, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Post created successfully:", response.data);
      // Reset form after submission
      setItems([{ name: "", quantity: "", deadline: "" }]);
    } catch (error) {
      console.error("Error creating post:", error.response?.data || error.message);
    }
  };

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h5" gutterBottom>
        Create Post
      </Typography>

      <TextField
        fullWidth
        label="Shop Name"
        value={shopName}
        disabled
        margin="normal"
        required
      />

      <Typography variant="h6" sx={{ mt: 2 }}>
        Items
      </Typography>

      {items.map((item, index) => (
        <Grid container spacing={2} key={index} sx={{ mb: 2 }}>
          <Grid item xs={4}>
            <TextField
              fullWidth
              label="Name"
              value={item.name}
              onChange={(e) => handleChange(index, "name", e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={4}>
            <TextField
              fullWidth
              label="Quantity"
              type="number"
              value={item.quantity}
              onChange={(e) => handleChange(index, "quantity", e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={4}>
            <TextField
              fullWidth
              label="Deadline"
              type="date"
              value={item.deadline}
              onChange={(e) => handleChange(index, "deadline", e.target.value)}
              InputLabelProps={{ shrink: true }}
              required
            />
          </Grid>
        </Grid>
      ))}

      <Button variant="outlined" onClick={handleAddRow} sx={{ mt: 2 }}>
        Add Row
      </Button>

      <Button variant="contained" color="primary" onClick={handleSubmit} sx={{ mt: 4 }}>
        Submit
      </Button>
    </Box>
  );
};

export default PostForm;
