import React, { useState } from 'react';
import axios from 'axios';

const ShopSearch = () => {
    const [query, setQuery] = useState('');
    const [shops, setShops] = useState([]);
    const [connections, setConnections] = useState({});

    const searchShops = async () => {
        try {
            const response = await axios.get(`/api/shops/search?query=${query}`);
            setShops(response.data);
        } catch (err) {
            console.error('Error searching shops', err);
        }
    };

    const sendConnectionRequest = async (shopId) => {
        try {
            await axios.post(`/api/shops/connect/${shopId}`);
            setConnections({ ...connections, [shopId]: 'Pending' });
        } catch (err) {
            console.error('Error sending connection request', err);
        }
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Search for shops"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
            />
            <button onClick={searchShops}>Search</button>

            <div>
                {shops.map((shop) => (
                    <div key={shop._id}>
                        <h3>{shop.shopName}</h3>
                        <p style={{ fontSize: '12px' }}>
                            {shop.shopCategory} - {shop.shopLocation}
                        </p>
                        <button
                            onClick={() => sendConnectionRequest(shop._id)}
                            disabled={connections[shop._id] === 'Pending'}
                        >
                            {connections[shop._id] || 'Send Connection'}
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ShopSearch;
