import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Grid2,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Button,
  useTheme,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

const Connections = () => {
  const [connections, setConnections] = useState({
    connected: [],
    pending: [],
    requested: [],
  });
  const [openDialog, setOpenDialog] = useState(false);
  const [shopToRemove, setShopToRemove] = useState(null);
  const theme = useTheme();

  // Fetch connections on component mount
  useEffect(() => {
    const fetchConnections = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/connections', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setConnections(response.data);
      } catch (err) {
        console.error('Error fetching connections', err);
      }
    };

    fetchConnections();
  }, []);

  // Remove a connection
  const handleRemoveConnection = async () => {
    try {
      await axios.post(`http://localhost:5000/api/remove/${shopToRemove._id}`,  {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });

      setConnections((prev) => ({
        ...prev,
        connected: prev.connected.filter((shop) => shop._id !== shopToRemove._id),
      }));
      setOpenDialog(false);
      setShopToRemove(null);
    } catch (err) {
      console.error('Error removing connection', err);
    }
  };

  // Re-request connection
  const handleRequestConnection = async (shopId) => {
    try {
      await axios.post(
        `http://localhost:5000/api/connect/${shopId}`,
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );

      alert('Connection request sent!');
    } catch (err) {
      console.error('Error requesting connection', err);
    }
  };

  return (
    <Box
      sx={{
        padding: '20px',
        minHeight: '100vh',
      }}
    >
      <Typography variant="h4" gutterBottom align="center">
        Manage Connections
      </Typography>

      {/* Connected Shops */}
      <Typography variant="h5" gutterBottom>
        Connected
      </Typography>
      <Grid2 container spacing={3}>
        {connections.connected.map((shop) => (
          <Grid2 item xs={12} sm={6} md={4} key={shop._id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{shop.shopName}</Typography>
                <IconButton
                  color="error"
                  onClick={() => {
                    setShopToRemove(shop);
                    setOpenDialog(true);
                  }}
                >
                  <DeleteIcon />
                </IconButton>
              </CardContent>
            </Card>
          </Grid2>
        ))}
      </Grid2>

      {/* Pending Shops */}
      <Typography variant="h5" gutterBottom sx={{ marginTop: 4 }}>
        Pending
      </Typography>
      <Grid2 container spacing={3}>
        {connections.pending.map((shop) => (
          <Grid2 item xs={12} sm={10} md={4} key={shop._id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{shop.shopName}</Typography>
              </CardContent>
            </Card>
          </Grid2>
        ))}
      </Grid2>

      {/* Requested Shops */}
      <Typography variant="h5" gutterBottom sx={{ marginTop: 4 }}>
        Requested
      </Typography>
      <Grid2 container spacing={3}>
        {connections.requested.map((shop) => (
          <Grid2 item xs={12} sm={6} md={4} key={shop._id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{shop.shopName}</Typography>
                <IconButton
                  color="primary"
                  onClick={() => handleRequestConnection(shop._id)}
                >
                  <AddIcon />
                </IconButton>
              </CardContent>
            </Card>
          </Grid2>
        ))}
      </Grid2>

      {/* Dialog for Confirmation */}
      <Dialog
        open={openDialog}
        onClose={() => setOpenDialog(false)}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">Remove Connection</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Are you sure you want to remove the connection with{' '}
            <strong>{shopToRemove?.shopName}</strong>?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleRemoveConnection} color="error" autoFocus>
            Remove
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Connections;
