import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  IconButton,
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import axios from 'axios';

const ShopSearch = ({ theme }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [shops, setShops] = useState([]);
  const [expandedShop, setExpandedShop] = useState(null);

  // Fetch shops based on the search query
  const fetchShops = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/searchshop?query=${searchQuery}`,
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setShops(response.data);
    } catch (error) {
      console.error('Error fetching shops:', error);
    }
  };

  // Trigger shop fetching whenever the search query changes
  useEffect(() => {
    if (searchQuery) {
      fetchShops();
    } else {
      setShops([]);
    }
  }, [searchQuery]);

  // Expand or collapse a shop's details
  const handleExpand = (shopId) => {
    setExpandedShop(expandedShop === shopId ? null : shopId);
  };

  // Handle connection or withdrawal actions
  const handleConnection = async (shopId, action) => {
    try {
      const endpoint =
        action === 'connect' ? `/api/connect/${shopId}` : `/api/withdraw/${shopId}`;
      await axios.post(`http://localhost:5000${endpoint}`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      fetchShops(); // Refresh the list to update connection statuses
    } catch (error) {
      console.error(`Error ${action === 'connect' ? 'connecting to' : 'withdrawing from'} shop:`, error);
    }
  };

  const isDarkMode = theme === 'dark';

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography
        variant="h4"
        gutterBottom
        align="center"
        sx={{
          color: isDarkMode ? '#00e1ff' : '#0047ff',
          textShadow: `0 0 10px ${isDarkMode ? '#00e1ff' : '#0047ff'}`,
        }}
      >
        Shop Search
      </Typography>
      <TextField
        fullWidth
        variant="outlined"
        placeholder="Search for shops..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        sx={{
          mb: 4,
          input: { color: isDarkMode ? '#fff' : '#000' },
          fieldset: { borderColor: isDarkMode ? '#00e1ff' : '#0047ff' },
          '& .MuiOutlinedInput-root:hover fieldset': {
            borderColor: isDarkMode ? '#00e1ff' : '#0047ff',
          },
        }}
      />
      <Grid container spacing={3}>
        {shops.map((shop) => (
          <Grid item xs={12} sm={6} md={4} key={shop._id}>
            <Card
              sx={{
                height: '100%',
                borderRadius: 4,
                padding: 2,
                background: isDarkMode
                  ? 'linear-gradient(145deg, #000428, #004e92)'
                  : 'linear-gradient(145deg, #d1eaff, #f0f8ff)',
                boxShadow: isDarkMode
                  ? '0 0 15px 6px rgba(0, 225, 255, 0.8)'
                  : '0 0 10px 4px rgba(0, 70, 255, 0.4)',
                color: isDarkMode ? '#fff' : '#000',
                transition: 'all 0.4s ease-in-out',
                '&:hover': {
                  boxShadow: isDarkMode
                    ? '0 0 25px 10px rgba(0, 225, 255, 1)'
                    : '0 0 20px 8px rgba(0, 70, 255, 0.8)',
                  transform: 'scale(1.05)',
                },
              }}
            >
              <CardContent>
                <Typography
                  variant="h6"
                  gutterBottom
                  sx={{
                    color: isDarkMode ? '#00e1ff' : '#0047ff',
                    textShadow: `0 0 8px ${isDarkMode ? '#00e1ff' : '#0047ff'}`,
                  }}
                >
                  {shop.shopName}
                </Typography>
                <Typography
                  variant="body2"
                  color="text.secondary"
                  sx={{
                    color: isDarkMode ? '#d4d4d4' : '#555',
                  }}
                >
                  {shop.shopCategory} â€¢ {shop.shopLocation}
                </Typography>
                <Typography
                  variant="body1"
                  sx={{ mt: 1, color: isDarkMode ? '#d4d4d4' : '#555' }}
                >
                  Owner: {shop.ownerName}
                </Typography>
                <IconButton
                  onClick={() => handleExpand(shop._id)}
                  sx={{
                    color: isDarkMode ? '#00e1ff' : '#0047ff',
                    '&:hover': { color: isDarkMode ? '#00b8cc' : '#0033cc' },
                  }}
                >
                  {expandedShop === shop._id ? <ExpandLessIcon /> : <ExpandMoreIcon />}
                </IconButton>
              </CardContent>
              <CardActions sx={{ justifyContent: 'center' }}>
                {shop.connectionStatus === 'connected' && (
                  <Button
                    variant="outlined"
                    onClick={() => handleConnection(shop._id, 'withdraw')}
                    sx={{
                      color: isDarkMode ? '#00e1ff' : '#0047ff',
                      borderColor: isDarkMode ? '#00e1ff' : '#0047ff',
                      '&:hover': {
                        backgroundColor: isDarkMode ? '#00e1ff' : '#0047ff',
                        color: isDarkMode ? '#000' : '#fff',
                      },
                    }}
                  >
                    Withdraw
                  </Button>
                )}
                {shop.connectionStatus === 'none' && (
                  <Button
                    variant="contained"
                    onClick={() => handleConnection(shop._id, 'connect')}
                    sx={{
                      backgroundColor: isDarkMode ? '#00e1ff' : '#0047ff',
                      color: isDarkMode ? '#000' : '#fff',
                      '&:hover': {
                        backgroundColor: isDarkMode ? '#00b8cc' : '#0033cc',
                      },
                    }}
                  >
                    Send Connection
                  </Button>
                )}
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default ShopSearch;
