import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Box,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  Collapse,
  IconButton,
  Switch,
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import axios from 'axios';

const ShopSearch = ({ toggleTheme, darkMode }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [shops, setShops] = useState([]);
  const [expandedShop, setExpandedShop] = useState(null);

  const fetchShops = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/searchshop?query=${searchQuery}`);
      setShops(response.data);
    } catch (error) {
      console.error('Error fetching shops:', error);
    }
  };

  useEffect(() => {
    if (searchQuery) {
      fetchShops();
    } else {
      setShops([]);
    }
  }, [searchQuery]);

  const handleExpand = (shopId) => {
    setExpandedShop(expandedShop === shopId ? null : shopId);
  };

  const handleConnection = async (shopId, action) => {
    try {
      await axios.post(`http://localhost:5000/api/shops/${shopId}/connect`, { action });
      fetchShops();
    } catch (error) {
      console.error('Error updating connection:', error);
    }
  };

  return (
    <Container>
      <Box display="flex" justifyContent="space-between" alignItems="center" my={2}>
        <Typography variant="h4">Shop Search</Typography>
        <Switch checked={darkMode} onChange={toggleTheme} />
      </Box>
      <TextField
        fullWidth
        variant="outlined"
        placeholder="Search for shops..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <List>
        {shops.map((shop) => (
          <Box key={shop._id} mb={2}>
            <ListItem
              button
              onClick={() => handleExpand(shop._id)}
              sx={{
                backgroundColor: 'primary.light',
                borderRadius: 1,
                mb: 1,
              }}
            >
              <ListItemText
                primary={shop.shopName}
                secondary={`${shop.shopCategory} â€¢ ${shop.shopLocation}`}
              />
              <IconButton>
                {expandedShop === shop._id ? <ExpandLessIcon /> : <ExpandMoreIcon />}
              </IconButton>
            </ListItem>
            <Collapse in={expandedShop === shop._id} timeout="auto" unmountOnExit>
              <Box p={2} border="1px solid" borderColor="primary.main" borderRadius={1}>
                <Typography variant="body1">Owner: {shop.ownerName}</Typography>
                <Typography variant="body2">
                  Category: {shop.shopCategory}
                </Typography>
                <Typography variant="body2">Location: {shop.shopLocation}</Typography>
                {shop.connectionStatus === 'connected' && (
                  <Button
                    variant="outlined"
                    color="secondary"
                    onClick={() => handleConnection(shop._id, 'withdraw')}
                  >
                    Withdraw
                  </Button>
                )}
                {shop.connectionStatus === 'pending' && (
                  <Button disabled>Pending</Button>
                )}
                {shop.connectionStatus === 'none' && (
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleConnection(shop._id, 'connect')}
                  >
                    Send Connection
                  </Button>
                )}
              </Box>
            </Collapse>
          </Box>
        ))}
      </List>
    </Container>
  );
};

export default ShopSearch;
