import React, { useState } from "react";
import {
  Box,
  Button,
  TextField,
  Typography,
  Paper,
  Card,
  CardContent,
  CardHeader,
} from "@mui/material";
import { useTheme } from "@mui/material/styles";

const Post = () => {
  const theme = useTheme(); // Access current theme
  const [post, setPost] = useState("");
  const [posts, setPosts] = useState([]);

  // Handle new post submission
  const handlePostSubmit = () => {
    if (post.trim()) {
      const newPost = {
        id: new Date().getTime(),
        content: post,
      };
      setPosts([newPost, ...posts]);
      setPost(""); // Reset the input field
    }
  };

  return (
    <Box
      sx={{
        padding: 2,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 3,
        backgroundColor: theme.palette.background.default,
        color: theme.palette.text.primary,
        minHeight: "100vh",
      }}
    >
      {/* Header */}
      <Typography variant="h4" component="h1" gutterBottom>
        Create a Post
      </Typography>

      {/* Post Input */}
      <Paper
        elevation={3}
        sx={{
          padding: 2,
          maxWidth: 500,
          width: "100%",
        }}
      >
        <TextField
          fullWidth
          label="What's on your mind?"
          variant="outlined"
          value={post}
          onChange={(e) => setPost(e.target.value)}
          multiline
          rows={3}
        />
        <Box
          sx={{
            marginTop: 2,
            display: "flex",
            justifyContent: "flex-end",
          }}
        >
          <Button
            variant="contained"
            color="primary"
            onClick={handlePostSubmit}
            disabled={!post.trim()}
          >
            Post
          </Button>
        </Box>
      </Paper>

      {/* Post List */}
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          gap: 2,
          width: "100%",
          maxWidth: 600,
        }}
      >
        {posts.length > 0 ? (
          posts.map((p) => (
            <Card key={p.id} sx={{ width: "100%" }}>
              <CardHeader
                title="You"
                subheader={new Date(p.id).toLocaleString()}
              />
              <CardContent>
                <Typography variant="body1">{p.content}</Typography>
              </CardContent>
            </Card>
          ))
        ) : (
          <Typography variant="body1" align="center">
            No posts to show. Start by creating one!
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default Post;
