import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { Link, useNavigate } from 'react-router-dom';
import { authService } from '../utils/authService';
import { AtSymbolIcon, LockClosedIcon } from '@heroicons/react/24/solid';

function Login() {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [loginError, setLoginError] = useState('');
  const navigate = useNavigate();

  const onSubmit = async (data) => {
    try {
      setLoginError('');
      await authService.login(data.email, data.password);
      navigate('/dashboard');
    } catch (error) {
      setLoginError(error.message || 'Login failed');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 to-primary-100 flex items-center justify-center px-4">
      <div className="bg-white shadow-2xl rounded-2xl max-w-md w-full p-8 space-y-6">
        <div className="text-center">
          <h2 className="text-3xl font-bold text-primary-700">Welcome Back</h2>
          <p className="text-gray-500 mt-2">Sign in to continue</p>
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <AtSymbolIcon className="h-5 w-5 text-gray-400" />
            </div>
            <input 
              type="email" 
              placeholder="Email Address"
              className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none 
                ${errors.email ? 'border-red-500' : 'border-gray-300'}
                focus:ring-2 focus:ring-primary-500`}
              {...register('email', { 
                required: 'Email is required',
                pattern: {
                  value: /\S+@\S+\.\S+/,
                  message: 'Invalid email address'
                }
              })}
            />
            {errors.email && (
              <p className="text-red-500 text-sm mt-1">{errors.email.message}</p>
            )}
          </div>

          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <LockClosedIcon className="h-5 w-5 text-gray-400" />
            </div>
            <input 
              type="password" 
              placeholder="Password"
              className={`w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none 
                ${errors.password ? 'border-red-500' : 'border-gray-300'}
                focus:ring-2 focus:ring-primary-500`}
              {...register('password', { 
                required: 'Password is required',
                minLength: {
                  value: 6,
                  message: 'Password must be at least 6 characters'
                }
              })}
            />
            {errors.password && (
              <p className="text-red-500 text-sm mt-1">{errors.password.message}</p>
            )}
          </div>

          {loginError && (
            <div className="bg-red-50 border border-red-300 text-red-700 px-4 py-2 rounded">
              {loginError}
            </div>
          )}

          <button 
            type="submit" 
            className="w-full bg-primary-700 text-white py-2 rounded-lg hover:bg-primary-600 transition duration-300"
          >
            Sign In
          </button>
        </form>

        <div className="text-center">
          <p className="text-gray-600">
            Don't have an account? {' '}
            <Link to="/register" className="text-primary-700 hover:underline">
              Register here
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
