import React, { useState } from "react";
import axios from "axios";
import { TextField, Grid, Card, CardContent, Typography, Box, CircularProgress, Button } from "@mui/material";

const ShopSearch = () => {
  const [shopName, setShopName] = useState("");
  const [shops, setShops] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedShop, setSelectedShop] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState(""); // For displaying status like pending, connected

  const handleSearch = async (e) => {
    setShopName(e.target.value);
    if (e.target.value.trim() === "") {
      setShops([]); // Clear results if search is empty
      return;
    }
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/searchshop?shopName=${e.target.value}`);
      setShops(response.data);
    } catch (error) {
      console.error("Error fetching shops:", error);
      setShops([]);
    }
    setLoading(false);
  };

  const handleShopClick = (shop) => {
    setSelectedShop(shop);
    // Check if the connection already exists and show status
    const connection = shop.connections.find(
      (conn) => conn.shopId === shop._id
    );
    setConnectionStatus(connection ? connection.status : "pending");
  };

  const handleSendConnection = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/sendConnection",
        { shopId: selectedShop._id },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      setConnectionStatus("pending");
      console.log(response.data.message);
    } catch (error) {
      console.error("Error sending connection:", error);
    }
  };

  return (
    <Box sx={{ padding: 2 }}>
      <TextField
        label="Search Shops by Name"
        variant="outlined"
        fullWidth
        value={shopName}
        onChange={handleSearch}
        sx={{ mb: 2 }}
      />
      
      {loading && <CircularProgress />}
      
      {shops.length > 0 && !loading && (
        <Grid container spacing={2}>
          {shops.map((shop, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Card sx={{ cursor: "pointer" }} onClick={() => handleShopClick(shop)}>
                <CardContent>
                  <Typography variant="h6">{shop.shopName}</Typography>
                  <Typography variant="body2">{shop.shopCategory}</Typography>
                  <Typography variant="body2">{shop.shopLocation}</Typography>
                  {connectionStatus === "pending" && (
                    <Button onClick={handleSendConnection}>Send Connection Request</Button>
                  )}
                  {connectionStatus === "connected" && (
                    <Typography variant="body2">Connected</Typography>
                  )}
                  {connectionStatus === "rejected" && (
                    <Typography variant="body2" color="error">Request Rejected</Typography>
                  )}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
      
      {selectedShop && connectionStatus && (
        <Box sx={{ mt: 3 }}>
          <Typography variant="h5">Shop Details</Typography>
          <Typography variant="h6">{selectedShop.shopName}</Typography>
          <Typography variant="body1">Category: {selectedShop.shopCategory}</Typography>
          <Typography variant="body1">Location: {selectedShop.shopLocation}</Typography>
          {connectionStatus === "pending" && (
            <Button onClick={handleSendConnection}>Send Connection Request</Button>
          )}
        </Box>
      )}
    </Box>
  );
};

export default ShopSearch;
