import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { authService } from '../services/authService';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateForm = () => {
    const newErrors = {};

    // Email validation
    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    // Password validation
    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    setFormData({ 
      ...formData, 
      [name]: value 
    });
    
    // Clear specific field error when user starts typing
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: undefined
      });
    }
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      try {
        const response = await authService.login(formData);
        // Assuming login returns a token or user info
        localStorage.setItem('token', response.token);
        navigate('/dashboard');
      } catch (err) {
        setErrors({
          submit: err.response?.data?.message || 'Login failed'
        });
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 to-primary-100 flex items-center justify-center px-4 py-8">
      <div className="bg-white shadow-2xl rounded-2xl max-w-md w-full p-8 space-y-6">
        <div className="text-center">
          <h2 className="text-3xl font-bold text-primary-700">Welcome Back</h2>
          <p className="text-gray-500 mt-2">Sign in to continue</p>
        </div>

        <form onSubmit={onSubmit} className="space-y-4">
          {/* Email Input */}
          <div>
            <input 
              type="email" 
              name="email"
              value={formData.email}
              onChange={onChange}
              placeholder="Email Address"
              className={`w-full px-4 py-2 border rounded-lg focus:outline-none 
                ${errors.email 
                  ? 'border-red-500 focus:ring-red-200' 
                  : 'border-gray-300 focus:ring-blue-200'} 
                focus:ring-2`}
            />
            {errors.email && (
              <p className="text-red-500 text-sm mt-1">{errors.email}</p>
            )}
          </div>

          {/* Password Input */}
          <div>
            <input 
              type="password" 
              name="password"
              value={formData.password}
              onChange={onChange}
              placeholder="Password"
              className={`w-full px-4 py-2 border rounded-lg focus:outline-none 
                ${errors.password 
                  ? 'border-red-500 focus:ring-red-200' 
                  : 'border-gray-300 focus:ring-blue-200'} 
                focus:ring-2`}
            />
            {errors.password && (
              <p className="text-red-500 text-sm mt-1">{errors.password}</p>
            )}
          </div>

          {/* Submit Error */}
          {errors.submit && (
            <div className="bg-red-50 border border-red-300 text-red-700 px-4 py-2 rounded">
              {errors.submit}
            </div>
          )}

          <button 
            type="submit" 
            className="w-full bg-primary-700 text-white py-2 rounded-lg 
            hover:bg-primary-600 transition duration-300 ease-in-out 
            transform hover:scale-[1.02] active:scale-95"
          >
            Sign In
          </button>
        </form>

        <div className="text-center">
          <p className="text-gray-600">
            Don't have an account? {' '}
            <Link 
              to="/register" 
              className="text-primary-700 hover:underline font-semibold"
            >
              Register here
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
