import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Register from './components/Register';
import Login from './components/Login';
import Logout from './components/Logout';
import DashboardLayoutNavigationLinks from './components/Dashboard';
import ProtectedRoute from './components/ProtectedRoute';
import './App.css';
import './index.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(() => !!localStorage.getItem('token'));

  const handleLogin = (token) => {
    // Save the token and update the state
    localStorage.setItem('token', token);
    setIsAuthenticated(true);
  };


  const handleLogout = () => {
    // Clear the token and update the state
    localStorage.removeItem('token');
    setIsAuthenticated(false);
  };

  useEffect(() => {
    // Validate token on load to ensure it's still valid
    const token = localStorage.getItem('token');
    if (token) {
      // Optionally validate the token here, e.g., by decoding it
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            isAuthenticated ? <Navigate to="/dashboard" /> : <Login onLogin={handleLogin} />
          }
        />
        <Route path="/register" element={<Register />} />
        <Route
          path="/login"
          element={isAuthenticated ? <Navigate to="/dashboard" /> : <Login onLogin={handleLogin} />}
        />
        <Route
          path="/logout"
          element={<Logout onLogout={handleLogout} />}
        />
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <DashboardLayoutNavigationLinks />
            </ProtectedRoute>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
