import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Connections = () => {
    const [connections, setConnections] = useState({ connected: [], pending: [], requested: [] });

    useEffect(() => {
        const fetchConnections = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/connections');
                setConnections(response.data);
            } catch (err) {
                console.error('Error fetching connections', err);
            }
        };

        fetchConnections();
    }, []);

    return (
        <div>
            <h2>Connections</h2>
            <div>
                <h3>Connected</h3>
                {connections.connected.map((shop) => (
                    <div key={shop._id}>{shop.shopName}</div>
                ))}
            </div>
            <div>
                <h3>Pending</h3>
                {connections.pending.map((shop) => (
                    <div key={shop._id}>{shop.shopName}</div>
                ))}
            </div>
            <div>
                <h3>Requested</h3>
                {connections.requested.map((shop) => (
                    <div key={shop._id}>{shop.shopName}</div>
                ))}
            </div>
        </div>
    );
};

export default Connections;
