import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Box,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  Collapse,
  IconButton,
  Card,
  CardContent,
  CardActions,
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import axios from 'axios';

const ShopSearch = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [shops, setShops] = useState([]);
  const [expandedShop, setExpandedShop] = useState(null);

  const fetchShops = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/searchshop?query=${searchQuery}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setShops(response.data);
    } catch (error) {
      console.error('Error fetching shops:', error);
    }
  };

  useEffect(() => {
    if (searchQuery) {
      fetchShops();
    } else {
      setShops([]);
    }
  }, [searchQuery]);

  const handleExpand = (shopId) => {
    setExpandedShop(expandedShop === shopId ? null : shopId);
  };

  const handleConnection = async (shopId, action) => {
    try {
      const endpoint = action === 'connect' ? `/api/connect/${shopId}` : `/api/withdraw/${shopId}`;
      await axios.post(`http://localhost:5000${endpoint}`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      fetchShops();
    } catch (error) {
      console.error('Error updating connection:', error);
    }
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom align="center">
        Shop Search
      </Typography>
      <TextField
        fullWidth
        variant="outlined"
        placeholder="Search for shops..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        sx={{ mb: 4 }}
      />
      <List>
        {shops.map((shop) => (
          <Card key={shop._id} variant="outlined" sx={{ mb: 2 }}>
            <ListItem
              button
              onClick={() => handleExpand(shop._id)}
              sx={{
                '&:hover': { backgroundColor: 'rgba(0, 0, 0, 0.04)' },
                borderBottom: expandedShop === shop._id ? '1px solid rgba(0, 0, 0, 0.12)' : 'none',
              }}
            >
              <ListItemText
                primary={<Typography variant="h6">{shop.shopName}</Typography>}
                secondary={
                  <Typography variant="body2" color="text.secondary">
                    {shop.shopCategory} • {shop.shopLocation}
                  </Typography>
                }
              />
              <IconButton>
                {expandedShop === shop._id ? <ExpandLessIcon /> : <ExpandMoreIcon />}
              </IconButton>
            </ListItem>
            <Collapse in={expandedShop === shop._id} timeout="auto" unmountOnExit>
              <CardContent>
                <Typography variant="body1">Owner: {shop.ownerName}</Typography>
                <Typography variant="body2" color="text.secondary">
                  Category: {shop.shopCategory}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Location: {shop.shopLocation}
                </Typography>
              </CardContent>
              <CardActions>
                {shop.connectionStatus === 'connected' && (
                  <Button
                    variant="outlined"
                    color="secondary"
                    onClick={() => handleConnection(shop._id, 'withdraw')}
                  >
                    Withdraw
                  </Button>
                )}
                {shop.connectionStatus === 'pending' && <Button disabled>Pending</Button>}
                {shop.connectionStatus === 'none' && (
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleConnection(shop._id, 'connect')}
                  >
                    Send Connection
                  </Button>
                )}
              </CardActions>
            </Collapse>
          </Card>
        ))}
      </List>
    </Container>
  );
};

export default ShopSearch;
